userDefinedLinkHintCss:
/* Vomnibar Gruvbox CSS */

#vomnibar ol, #vomnibar ul {
  list-style: none;
  display: none;
}

#vomnibar {
  display: block;
  position: fixed;
  width: calc(100% - 20px); /* adjusted to keep border radius and box-shadow visible*/
  /*min-width: 400px;
  top: 70px;
  left: 50%;*/
  top: 8px;
  left: 8px;
  /*margin: 0 0 0 -40%;*/
  font-family: sans-serif;
  background-color: #1d2021;
  text-align: left;
  border: 2px solid #ebdbb2;
  border-radius: 0px;
  z-index: 2139999999; /* One less than hint markers and the help dialog (see ../content_scripts/vimium.css). */
}

#vomnibar input {
  color: #ebdbb2;
  font-family: 'Noto Sans', sans-serif;
  font-size: 14px;
  height: 34px;
  margin-bottom: 0;
  padding: 4px;
  background-color: #282828;
  border: 1px solid #ebdbb2;
  border-radius: 0px;
  width: 100%;
  outline: none;
  box-sizing: border-box;
}

#vomnibar .vomnibarSearchArea {
  display: block;
  padding: 10px;
  background-color: #1d2021;
  border-bottom: 1px solid #1d2021;
}

#vomnibar ul {
  background-color: #1d2021;
  list-style: none;
  padding: 10px 0;
  padding-top: 0;
}

#vomnibar li {
  border-bottom: 1px solid #282828;
  line-height: 1.1em;
  padding: 7px 10px;
  font-size: 14px;
  color: #ebdbb2;
  position: relative;
  display: list-item;
  margin: auto;
}

#vomnibar li:last-of-type {
  border-bottom: none;
}

#vomnibar li .vomnibarTopHalf, #vomnibar li .vomnibarBottomHalf {
  display: block;
  overflow: hidden;
}

#vomnibar li .vomnibarBottomHalf {
  font-size: 15px;
  margin-top: 3px;
  padding: 2px 0;
}

#vomnibar li .vomnibarIcon {
  background-position-y: center;
  background-size: 16px;
  background-repeat: no-repeat;
  padding-left: 20px;
}

#vomnibar li .vomnibarSource {
  color: #fabd2f;
  margin-right: 4px;
}
#vomnibar li .vomnibarRelevancy {
  position: absolute;
  right: 0;
  top: 0;
  padding: 5px;
  background-color: #797979;
  color: white;
  font-family: monospace;
  width: 100px;
  overflow: hidden;
}

#vomnibar li .vomnibarUrl {
  white-space: nowrap;
  color: #458588;
}

#vomnibar li .vomnibarMatch {
  font-weight: bold;
  color: #83a598;
}

#vomnibar li em, #vomnibar li .vomnibarTitle {
  color: #a89984;
  margin-left: 4px;
  font-weight: normal;
}
#vomnibar li em { font-style: italic; }
#vomnibar li em .vomnibarMatch, #vomnibar li .vomnibarTitle .vomnibarMatch {
  color: #ebdbb2;
}

#vomnibar li.vomnibarSelected {
  background-color: #282828;
  font-weight: normal;
}

#vomnibarInput::selection {
  background-color: #a89984;
}

.vomnibarInsertText {
}

.vomnibarNoInsertText {
  visibility: hidden;
}

div > .vimiumHintMarker {
  padding: 3px 4px;
  background-color: #1d2021;
  border: 1px solid #ebdbb2;
  border-radius: 0px;
  background-image: none;
}

div > .vimiumHintMarker span {
  font-size: 12px;
  font-weight: bold;
  text-shadow: none;
  color: #ebdbb2;
  background-color: #1d2021;
}

div > .vimiumHintMarker > .matchingCharacter {
  color: #a89984;
}
